package java_day3_practice;

public class ConditionalStatementsPractice_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//if statement
		int number = 5;
		if(number>0)
		{
			System.out.println(number + " " + "is a Natural number");
		}
		
		//if else statement
		int first_value = 10, second_value = 20;
		if(first_value>second_value)
		{
			System.out.println("First value is greater");
		}
		else
		{
			System.out.println("Second value is greater");
		}
	}

}
		
		