package java_day3_practice;

public class ConditionalStatementsPractice_02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//if else ladder
		int first_value = 100, second_value = 100, third_value = 100;
		if(first_value>second_value && first_value>second_value)
		{
			System.out.println("first value is greatest");
		}
		else if(second_value>first_value && second_value>third_value)
		{
			System.out.println("Second value is greatest");
		}
		else
		{
			System.out.println("Third value is greatest");
		}
	
	}

}
